export function defineMapboxControl(onAdd, onRemove) {
  class CustomControl {
    constructor() {
    }
    onAdd(map) {
      const html = onAdd(map);
      html.remove();
      return html;
    }
    onRemove(map) {
      onRemove(map);
    }
  }
  const control = new CustomControl();
  control.onAdd = onAdd;
  control.onRemove = onRemove;
  return control;
}
